package com.sunzn.pay.alipayimport android.text.TextUtilsclass AliPayResult(payResult: Map<String, String>?) {    var resultStatus: String? = null        private set    var result: String? = null        private set    var memo: String? = null        private set    init {        for (key in payResult!!.keys) {            when {                TextUtils.equals(key, "resultStatus") -> resultStatus = payResult[key]                TextUtils.equals(key, "result") -> result = payResult[key]                TextUtils.equals(key, "memo") -> memo = payResult[key]            }        }    }}