package com.sunzn.pay

object OrderProxy {

    val RSA2_PRIVATE = "";

    fun buildOrder(appID: String): String {
        val params = OrderInfoUtil2_0.buildOrderParamMap(appID, true)
        val orderParam = OrderInfoUtil2_0.buildOrderParam(params)
        val sign = OrderInfoUtil2_0.getSign(params, RSA2_PRIVATE, true)
        val orderInfo = "$orderParam&$sign"
        return orderInfo
    }

}